local dragging_offset_x, dragging_offset_x = 0, 0
local is_dragging = false

local function inRect(x,y,x1,y1,x2,y2)
    if (x >= x1 and x <= x2) and (y >= y1 and y <= y2) then 
        return true 
    else 
        return false 
    end
end

local function length2D(vec1, vec2)

    local x = vec1.x - vec2.x
    local y = vec1.y - vec2.y

    return math.sqrt(x^2 + y^2)

end

local function dragHandler()
    local mouse_x, mouse_y = input.GetMousePos();
  
    if (is_dragging == true) then
        boardpos.x = mouse_x - dragging_offset_x;
        boardpos.y = mouse_y - dragging_offset_y;
        return;
    end
  
    if (mouse_x >= boardpos.x and mouse_x <= (boardpos.x + (#board * 20)) and mouse_y >= boardpos.y and mouse_y <= boardpos.y + (#board[1] * 20)) then
        is_dragging = true;
        dragging_offset_x = mouse_x - boardpos.x;
        dragging_offset_y = mouse_y - boardpos.y;
        return;
    end
end

