local function createboard()

    local dots = 0

    for x = 1, 28 do
        local line = {}
        for y = 1, 36 do
            local pos = {}

            if mapinfo[x][y][1] then
                pos.isSolid = true
                pos.walltype = mapinfo[x][y][2]
                pos.isFood = false
                pos.isPower = false
            else
                pos.isSolid = false
                pos.walltype = 0
                if mapinfo[x][y][2] == 1 then
                    pos.isFood = true
                    dots = dots + 1
                else
                    pos.isFood = false
                end
                if mapinfo[x][y][2] == 2 then
                    pos.isPower = true
                else
                    pos.isPower = false
                end
            end

            if y == 15 and (x > 11 and x < 18) then
                pos.noTurn = true
            elseif y == 27 and (x > 11 and x < 18) then
                pos.noTurn = true
            else
                pos.noTurn = false
            end

            if (y > 16 and y < 20) and (x > 11 and x < 18) then
                pos.ghostSpawn = true
            elseif y == 16 and (x == 14 or x == 15) then
                pos.ghostSpawn = true
            else
                pos.ghostSpawn = false
            end

            if y == 18 then
                if x == 1 or x == 28 then
                    pos.isTeleporter = true
                else
                    pos.isTeleporter = false
                end
                if x < 7 or x > 22 then
                    pos.isTunnel = true
                else
                    pos.isTunnel = false
                end
            else
                pos.isTeleporter = false
                pos.isTunnel = false
            end

            table.insert(line, pos) 
        end
        table.insert(board, line)
    end

    game.dots = dots
end

local function changeLevel(lvl)

    if lvl > 21 then lvl = 21 end

    player.speed = levelsettings[lvl].pSpeed
    player.dotspeed = levelsettings[lvl].pDotSpeed
    player.powerspeed = levelsettings[lvl].pFrightSpeed
    player.powerdotspeed = levelsettings[lvl].pFrightDotSpeed
    for k, v in pairs(ghosts) do
        ghosts[k].speed = levelsettings[lvl].gSpeed
        ghosts[k].tunnelspeed = levelsettings[lvl].gTunnelSpeed
        ghosts[k].frightspeed = levelsettings[lvl].gFrightSpeed
    end

end

local function getGhostGoal(ghost)
    if ghost == "blinky" then
        ghosts.blinky.goal.x = player.position.x
        ghosts.blinky.goal.y = player.position.y
    elseif ghost == "pinky" then
        if player.direction == "up" then
            ghosts.pinky.goal.x = player.position.x - 4
            ghosts.pinky.goal.y = player.position.y - 4
        elseif player.direction == "down" then
            ghosts.pinky.goal.x = player.position.x
            ghosts.pinky.goal.y = player.position.y + 4
        elseif player.direction == "left" then
            ghosts.pinky.goal.x = player.position.x - 4
            ghosts.pinky.goal.y = player.position.y
        elseif player.direction == "right" then
            ghosts.pinky.goal.x = player.position.x + 4
            ghosts.pinky.goal.y = player.position.y
        end
    elseif ghost == "inky" then
        if player.direction == "up" then
            ghosts.inky.temppos.x = player.position.x - 2
            ghosts.inky.temppos.y = player.position.y - 2
        elseif player.direction == "down" then
            ghosts.inky.temppos.x = player.position.x
            ghosts.inky.temppos.y = player.position.y + 2
        elseif player.direction == "left" then
            ghosts.inky.temppos.x = player.position.x - 2
            ghosts.inky.temppos.y = player.position.y
        elseif player.direction == "right" then
            ghosts.inky.temppos.x = player.position.x + 2
            ghosts.inky.temppos.y = player.position.y
        end

        local vector = {x = ghosts.blinky.position.x - ghosts.inky.temppos.x, y = ghosts.blinky.position.y - ghosts.inky.temppos.y}

        ghosts.inky.goal.x = ghosts.inky.temppos.x - vector.x
        ghosts.inky.goal.y = ghosts.inky.temppos.y - vector.y

    elseif ghost == "clyde" then

        if length2D(player.position, ghosts.clyde.position) <= 8 then
            ghosts.clyde.goal.x = ghosts.clyde.scattergoal.x
            ghosts.clyde.goal.y = ghosts.clyde.scattergoal.y
        else
            ghosts.clyde.goal.x = player.position.x
            ghosts.clyde.goal.y = player.position.y
        end

    end
end

local function getTargetPos(dir)

    if dir == "left" then
        if board[player.position.x - 1][player.position.y].isSolid or board[player.position.x - 1][player.position.y].ghostSpawn then
            player.move = false
            player.target.x = nil
            player.target.y = nil
        else
            player.move = true
            player.target.x = player.position.x - 1
            player.target.y = player.position.y
        end
    elseif dir == "right" then
        if board[player.position.x + 1][player.position.y].isSolid or board[player.position.x + 1][player.position.y].ghostSpawn then
            player.move = false
            player.target.x = nil
            player.target.y = nil
        else
            player.move = true
            player.target.x = player.position.x + 1
            player.target.y = player.position.y
        end
    elseif dir == "up" then
        if board[player.position.x][player.position.y - 1].isSolid or board[player.position.x][player.position.y - 1].ghostSpawn then
            player.move = false
            player.target.x = nil
            player.target.y = nil
        else
            player.move = true
            player.target.x = player.position.x
            player.target.y = player.position.y - 1
        end
    elseif dir == "down" then
        if board[player.position.x][player.position.y + 1].isSolid or board[player.position.x][player.position.y + 1].ghostSpawn then
            player.move = false
            player.target.x = nil
            player.target.y = nil
        else
            player.move = true
            player.target.x = player.position.x
            player.target.y = player.position.y + 1
        end
    end

end

local function switchdirection(dir)

    if player.move == false then
        player.direction = dir
        getTargetPos(player.direction)
    end

    if dir == "left" then
        player.nextdirection = "left"
        if player.direction == "right" then
            player.direction = player.nextdirection
            player.position.x = player.target.x
            player.position.y = player.target.y
            player.arrival = 1 - player.arrival
            getTargetPos(player.direction)
        end
    elseif dir == "right" then 
        player.nextdirection = "right"
        if player.direction == "left" then
            player.direction = player.nextdirection
            player.position.x = player.target.x
            player.position.y = player.target.y
            player.arrival = 1 - player.arrival
            getTargetPos(player.direction)
        end
    elseif dir == "up" then
        player.nextdirection = "up"
        if player.direction == "down" then
            player.direction = player.nextdirection
            player.position.x = player.target.x
            player.position.y = player.target.y
            player.arrival = 1 - player.arrival
            getTargetPos(player.direction)
        end
    elseif dir == "down" then
        player.nextdirection = "down"
        if player.direction == "up" then
            player.direction = player.nextdirection
            player.position.x = player.target.x
            player.position.y = player.target.y
            player.arrival = 1 - player.arrival
            getTargetPos(player.direction)
        end
    end

end

local function setdirection(dir)

    if (player.direction == "left" or player.direction == "right") and (dir == "up" or dir == "down") then
        if dir == "up" then 
            if board[player.position.x][player.position.y - 1].isSolid or board[player.position.x][player.position.y - 1].ghostSpawn then return end
        elseif dir == "down" then
            if board[player.position.x][player.position.y + 1].isSolid or board[player.position.x][player.position.y + 1].ghostSpawn then return end
        end
    elseif (player.direction == "up" or player.direction == "down") and (dir == "left" or dir == "right") then
        if dir == "left" then 
            if board[player.position.x - 1][player.position.y].isSolid or board[player.position.x - 1][player.position.y].ghostSpawn then return end
        elseif dir == "right" then
            if board[player.position.x + 1][player.position.y].isSolid or board[player.position.x + 1][player.position.y].ghostSpawn then return end
        end
    end

    player.direction = dir

end

local function GetMove(ghost)

    local savedpos = nil
    local saveddir = nil
    local debugcount = 0
    local debugstart = false

    ::debug::

    local inspawn = false

    if board[ghosts[ghost].position.x][ghosts[ghost].position.y].ghostSpawn then
        inspawn = true
    end

    local pDirections = {up = true, left = true, down = true, right = true}

    if ghosts[ghost].direction == "up" then
        pDirections.down = false
    elseif ghosts[ghost].direction == "down" then
        pDirections.up = false
    elseif ghosts[ghost].direction == "left" then
        pDirections.right = false
    elseif ghosts[ghost].direction == "right" then
        pDirections.left = false
    end

    if board[ghosts[ghost].position.x][ghosts[ghost].position.y].noTurn then
        pDirections.down = false
        pDirections.up = false
    end

    if pDirections.up then
        if board[ghosts[ghost].position.x][ghosts[ghost].position.y - 1].isSolid then
            pDirections.up = false
        end
    end
    if pDirections.left then
        if board[ghosts[ghost].position.x - 1][ghosts[ghost].position.y].isSolid then
            pDirections.left = false
        end
    end
    if pDirections.down then
        if board[ghosts[ghost].position.x][ghosts[ghost].position.y + 1].isSolid then
            pDirections.down = false
        end
    end
    if pDirections.right then
        if board[ghosts[ghost].position.x + 1][ghosts[ghost].position.y].isSolid then
            pDirections.right = false
        end
    end

    local bestDirection = nil
    local bestDistance = 99999999

    if pDirections.up then
        local target = {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y - 1}
        local distance = length2D(ghosts[ghost].goal, target)

        if distance < bestDistance then
            bestDistance = distance
            bestDirection = "up"
        end
    end
    if pDirections.left then
        local target = {x = ghosts[ghost].position.x - 1, y = ghosts[ghost].position.y}
        local distance = length2D(ghosts[ghost].goal, target)

        if distance < bestDistance then
            bestDistance = distance
            bestDirection = "left"
        end
    end
    if pDirections.down then
        local target = {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y + 1}
        local distance = length2D(ghosts[ghost].goal, target)

        if distance < bestDistance then
            bestDistance = distance
            bestDirection = "down"
        end
    end
    if pDirections.right then
        local target = {x = ghosts[ghost].position.x + 1, y = ghosts[ghost].position.y}
        local distance = length2D(ghosts[ghost].goal, target)

        if distance < bestDistance then
            bestDistance = distance
            bestDirection = "right"
        end
    end
    
    if bestDirection == nil then
        if ghosts[ghost].direction == "up" then
            bestDirection = "down"
        elseif ghosts[ghost].direction == "down" then
            bestDirection = "up"
        elseif ghosts[ghost].direction == "left" then
            bestDirection = "right"
        elseif ghosts[ghost].direction == "right" then
            bestDirection = "left"
        end
    end

    if inspawn then
        if ghosts[ghost].position.x < 14 then
            bestDirection = "right"
        elseif ghosts[ghost].position.x > 15 then
            bestDirection = "left"
        elseif ghosts[ghost].position.y > 15 then
            bestDirection = "up"
        end
    end

    ghosts[ghost].direction = bestDirection

    if debug then

        if not debugstart then
            debugstart = true
            savedpos = {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y}
            saveddir = bestDirection
            ghosts[ghost].path = {}
        end

        if debugcount <= 10 then
            local target = {x = nil, y = nil}

            if bestDirection == "up" then
                target = {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y - 1}
            elseif bestDirection == "left" then
                target = {x = ghosts[ghost].position.x - 1, y = ghosts[ghost].position.y}
            elseif bestDirection == "down" then
                target = {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y + 1}
            elseif bestDirection == "right" then
                target = {x = ghosts[ghost].position.x + 1, y = ghosts[ghost].position.y}
            end

            table.insert(ghosts[ghost].path, target)
            ghosts[ghost].position.x = target.x
            ghosts[ghost].position.y = target.y

            if board[ghosts[ghost].position.x][ghosts[ghost].position.y].isTeleporter then
                if ghosts[ghost].position.x == 1 then 
                    ghosts[ghost].position.x = 28
                elseif ghosts[ghost].position.x == 28 then
                    ghosts[ghost].position.x = 1
                end
                table.insert(ghosts[ghost].path, {x = ghosts[ghost].position.x, y = ghosts[ghost].position.y})
            end

            debugcount = debugcount + 1

            goto debug

        else

            ghosts[ghost].direction = saveddir
            ghosts[ghost].position.x = savedpos.x
            ghosts[ghost].position.y = savedpos.y

        end

    end

end

local function GetFrightMove(ghost)

    local pDirections = {up = true, left = true, down = true, right = true}

    if ghosts[ghost].direction == "up" then
        pDirections.down = false
    elseif ghosts[ghost].direction == "down" then
        pDirections.up = false
    elseif ghosts[ghost].direction == "left" then
        pDirections.right = false
    elseif ghosts[ghost].direction == "right" then
        pDirections.left = false
    end

    if board[ghosts[ghost].position.x][ghosts[ghost].position.y].noTurn then
        pDirections.down = false
        pDirections.up = false
    end

    if pDirections.up then
        if board[ghosts[ghost].position.x][ghosts[ghost].position.y - 1].isSolid then
            pDirections.up = false
        end
    end
    if pDirections.left then
        if board[ghosts[ghost].position.x - 1][ghosts[ghost].position.y].isSolid then
            pDirections.left = false
        end
    end
    if pDirections.down then
        if board[ghosts[ghost].position.x][ghosts[ghost].position.y + 1].isSolid then
            pDirections.down = false
        end
    end
    if pDirections.right then
        if board[ghosts[ghost].position.x + 1][ghosts[ghost].position.y].isSolid then
            pDirections.right = false
        end
    end

    local tempdirections = {}

    for k, v in pairs(pDirections) do
        if v then
            table.insert(tempdirections, k)
        end
    end

    ghosts[ghost].direction = tempdirections[math.random(1, #tempdirections)]

end

local function GetGhostTarget(ghost)

