    elseif ghosts[ghost].direction == "down" then
        ghosts[ghost].target.x = ghosts[ghost].position.x
        ghosts[ghost].target.y = ghosts[ghost].position.y + 1
    elseif ghosts[ghost].direction == "left" then
        ghosts[ghost].target.x = ghosts[ghost].position.x - 1
        ghosts[ghost].target.y = ghosts[ghost].position.y
    elseif ghosts[ghost].direction == "right" then
        ghosts[ghost].target.x = ghosts[ghost].position.x + 1
        ghosts[ghost].target.y = ghosts[ghost].position.y
    end

end

local function FlipGhost(ghost)
    if ghosts[ghost].direction == "up" then
        ghosts[ghost].direction = "down"
    elseif ghosts[ghost].direction == "down" then
        ghosts[ghost].direction = "up"
    elseif ghosts[ghost].direction == "left" then
        ghosts[ghost].direction = "right"
    elseif ghosts[ghost].direction == "right" then
        ghosts[ghost].direction = "left"
    end
    ghosts[ghost].position.x = ghosts[ghost].target.x
    ghosts[ghost].position.y = ghosts[ghost].target.y
    ghosts[ghost].arrival = 1 - ghosts[ghost].arrival
end

local function switchstate(state)

    for k, v in pairs(ghosts) do
        if v.state ~= "fright" and v.state ~= "dead" then
            if state == "scatter" then
                ghosts[k].goal.x = ghosts[k].scattergoal.x
                ghosts[k].goal.y = ghosts[k].scattergoal.y
                ghosts[k].state = "scatter"
            else
                getGhostGoal(k)
                ghosts[k].state = "chase"
            end
            FlipGhost(k)
            GetGhostTarget(k)
        end
    end

end

