local function ghostlogic(ghost)

    local speed = 0

    if board[ghosts[ghost].position.x][ghosts[ghost].position.y].isTunnel then
        speed = ghosts[ghost].tunnelspeed
    elseif ghosts[ghost].state == "fright" then
        speed = ghosts[ghost].frightspeed
    else
        speed = ghosts[ghost].speed
    end

    if ghosts[ghost].arrival >= 1 then

        ghosts[ghost].arrival = ghosts[ghost].arrival - 1

        ghosts[ghost].animstate = ghosts[ghost].animstate + 1
        if ghosts[ghost].animstate > 2 then ghosts[ghost].animstate = 1 end

        ghosts[ghost].position.x = ghosts[ghost].target.x
        ghosts[ghost].position.y = ghosts[ghost].target.y

        if board[ghosts[ghost].position.x][ghosts[ghost].position.y].isTeleporter then
            if ghosts[ghost].position.x == 1 and ghosts[ghost].direction == "left" then
                ghosts[ghost].position.x = 28
            end
            if ghosts[ghost].position.x == 28 and ghosts[ghost].direction == "right" then
                ghosts[ghost].position.x = 1
            end
        end

        if ghosts[ghost].state == "chase" then
            getGhostGoal(ghost)
        elseif ghosts[ghost].state == "scatter" then
            ghosts[ghost].goal.x = ghosts[ghost].scattergoal.x
            ghosts[ghost].goal.y = ghosts[ghost].scattergoal.y
        elseif ghosts[ghost].state == "dead" then
            ghosts[ghost].goal.x = 14
            ghosts[ghost].goal.y = 15
        end

        if ghosts[ghost].state == "fright" then
            GetFrightMove(ghost)
        else
            GetMove(ghost)
        end

        if ghosts[ghost].state == "dead" and ghosts[ghost].position.x == 14 and (ghosts[ghost].position.y == 15 or ghosts[ghost].position.y == 16) then
            ghosts[ghost].direction = "down"
        end

        if ghosts[ghost].state == "dead" and ghosts[ghost].position.y ~= 15 and board[ghosts[ghost].position.x][ghosts[ghost].position.y].ghostSpawn then
            ghosts[ghost].state = game.curstate
            ghosts[ghost].flash = 0
        end

        GetGhostTarget(ghost)

    end

    ghosts[ghost].arrival = ghosts[ghost].arrival + (globals.AbsoluteFrameTime() * speed)

    if ghosts[ghost].state == "fright" and globals.CurTime() >= ghosts[ghost].frighttime + levelsettings[game.clvl].frightTime then
        ghosts[ghost].state = game.curstate
        ghosts[ghost].flash = 0
        FlipGhost(ghost)
        if game.curstate == "scatter" then
            ghosts[ghost].goal.x = ghosts[ghost].scattergoal.x
            ghosts[ghost].goal.y = ghosts[ghost].scattergoal.y
        else
            getGhostGoal(ghost)
        end
        GetGhostTarget(ghost)
    end
    
    if ghosts[ghost].direction == "left" then
        ghosts[ghost].realpos.x = ghosts[ghost].position.x - ghosts[ghost].arrival + 0.5
        ghosts[ghost].realpos.y = ghosts[ghost].position.y + 0.5
    elseif ghosts[ghost].direction == "right" then
        ghosts[ghost].realpos.x = ghosts[ghost].position.x + ghosts[ghost].arrival + 0.5
        ghosts[ghost].realpos.y = ghosts[ghost].position.y + 0.5
    elseif ghosts[ghost].direction == "up" then
        ghosts[ghost].realpos.x = ghosts[ghost].position.x + 0.5
        ghosts[ghost].realpos.y = ghosts[ghost].position.y - ghosts[ghost].arrival + 0.5
    elseif ghosts[ghost].direction == "down" then
        ghosts[ghost].realpos.x = ghosts[ghost].position.x + 0.5
        ghosts[ghost].realpos.y = ghosts[ghost].position.y + ghosts[ghost].arrival + 0.5
    end

end

local function main()

    if not game.paused then

        if input.IsButtonPressed(37) then
            switchdirection("left")
        end
        if input.IsButtonPressed(39) then
            switchdirection("right")
        end
        if input.IsButtonPressed(38) then
            switchdirection("up")
        end
        if input.IsButtonPressed(40) then
            switchdirection("down")
        end

        if game.started and game.ready and not player.dead and game.levelswitch == 0 then

            local playerspeed = 0

            if player.powerup then
                if player.target.x ~= nil and player.target.y ~= nil then
                    if board[player.target.x][player.target.y].isFood then
                        playerspeed = player.powerdotspeed
                    else
                        playerspeed = player.powerspeed
                    end
                else
                    playerspeed = player.powerspeed
                end
            else
                if player.target.x ~= nil and player.target.y ~= nil then
                    if board[player.target.x][player.target.y].isFood then
                        playerspeed = player.dotspeed
                    else
                        playerspeed = player.speed
                    end
                else
                    playerspeed = player.powerspeed
                end
            end

            if player.move then
                if player.arrival >= 1 then
                    player.arrival = player.arrival - 1
                    player.position.x = player.target.x
                    player.position.y = player.target.y

                    if board[player.position.x][player.position.y].isTeleporter then
                        if player.position.x == 1 and player.direction == "left" then
                            player.position.x = 28
                        end
                        if player.position.x == 28 and player.direction == "right" then
                            player.position.x = 1
                        end
                    end

                    if board[player.position.x][player.position.y].isFood then
                        game.dots = game.dots - 1
                        board[player.position.x][player.position.y].isFood = false
                        game.score = game.score + 10
                        if game.dots == 170 then
                            game.bonus = {x = 15, y = 21.5}
                        end
                    end

                    if board[player.position.x][player.position.y].isPower then
                        board[player.position.x][player.position.y].isPower = false
                        if levelsettings[game.clvl].frightTime ~= 0 then
                            player.powerup = true
                            player.poweruptime = globals.CurTime()
                            for k, v in pairs(ghosts) do 
                                if v.state ~= "dead" then
                                    FlipGhost(k)
                                    ghosts[k].state = "fright"
                                    ghosts[k].lastflash = 0
                                    ghosts[k].flash = 0
                                    ghosts[k].frighttime = globals.CurTime()
                                    GetGhostTarget(k)
                                    ghosts[k].path = {}
                                end
                            end
                        end
                        game.score = game.score + 50
                    end

                    if player.powerup and globals.CurTime() >= player.poweruptime + levelsettings[game.clvl].frightTime then
                        player.powerup = false
                        player.ghoststreak = 1
                    end

                    setdirection(player.nextdirection)
                    getTargetPos(player.direction)

                    if player.target.x == nil then 
                        player.animstate = 1 
                        player.arrival = 0
                        player.realpos.x = player.position.x + 0.5
                        player.realpos.y = player.position.y + 0.5
                    else
                        player.animstate = player.animstate + 1
                        if player.animstate > 4 then player.animstate = 1 end
                    end

                    for k, v in pairs(ghosts) do 
                        if v.state == "chase" then
                            getGhostGoal(k)
                        end
                    end
                    
                end

                player.arrival = player.arrival + (globals.AbsoluteFrameTime() * playerspeed)
                if player.direction == "left" then
                    player.realpos.x = player.position.x - player.arrival + 0.5
                    player.realpos.y = player.position.y + 0.5
                elseif player.direction == "right" then
                    player.realpos.x = player.position.x + player.arrival + 0.5
                    player.realpos.y = player.position.y + 0.5
                elseif player.direction == "up" then
                    player.realpos.x = player.position.x + 0.5
                    player.realpos.y = player.position.y - player.arrival + 0.5
                elseif player.direction == "down" then
                    player.realpos.x = player.position.x + 0.5
                    player.realpos.y = player.position.y + player.arrival + 0.5
                end

            end

            if game.stage <= 7 then
                local templvl = nil

                if game.level == 1 then
                    templvl = 1
                elseif game.level >= 2 and game.level <= 4 then
                    templvl = 2
                else
                    templvl = 3
                end

                if game.stagetime >= timings[templvl][game.stage].time then
                    game.stage = game.stage + 1
                    game.stagetime = 0
                    if game.stage >= 8 then
                        switchstate("chase")
                        game.curstate = "chase"
                    else
                        switchstate(timings[templvl][game.stage].state)
                        game.curstate = timings[templvl][game.stage].state
                    end
                else
                    game.stagetime = game.stagetime + globals.AbsoluteFrameTime()
                end

            end

            if game.dots <= levelsettings[game.clvl].s2 then
                ghosts.blinky.speed = levelsettings[game.clvl].s2Speed
            elseif game.dots <= levelsettings[game.clvl].s1 then
                ghosts.blinky.speed = levelsettings[game.clvl].s1Speed
            end

            for k, v in pairs(ghosts) do
                ghostlogic(k)
                if length2D(player.realpos, v.realpos) <= 0.5 then
                    if player.powerup and v.state == "fright" then
                        v.state = "dead"
                        local temps = 0
                        if player.ghoststreak == 1 then
                            game.score = game.score + 200
                            temps = 200
                        elseif player.ghoststreak == 2 then
                            game.score = game.score + 400
                            temps = 400
                        elseif player.ghoststreak == 3 then
                            game.score = game.score + 800
                            temps = 800
                        else
                            game.score = game.score + 1600
                            temps = 1600
                        end
                        player.ghoststreak = player.ghoststreak + 1
                        local score = {
                            pos = {x = v.realpos.x - 0.5, y = v.realpos.y - 0.5},
                            color = {125,125,255,255},
                            time = globals.CurTime(),
                            amount = temps
                        }
                        table.insert(scores, score)
                    elseif v.state ~= "dead" then
                        player.dead = true
                        player.deadtime = globals.CurTime()
                        game.lives = game.lives - 1
                        if game.lives <= 0 then
                            if game.score > game.highscore then
                                game.highscore = game.score
                                writescore()
                            end
                        end
                    end
                end
                if v.state == "fright" then
                    if (globals.CurTime() - v.frighttime) > (levelsettings[game.clvl].frightTime - ((levelsettings[game.clvl].flashes * 2) - 1) * 0.166) then
                        if v.lastflash == 0 or v.lastflash + 0.166 <= globals.CurTime() then
                            ghosts[k].lastflash = globals.CurTime()
                            if v.flash == 2 then
                                ghosts[k].flash = 0
                            else
                                ghosts[k].flash = 2
                            end
                        end
                    end
                end
            end

            if game.bonus ~= nil then
                if length2D(player.realpos, game.bonus) <= 0.5 then
                    game.score = game.score + levelsettings[game.clvl].bonus
                    local score = {
                        pos = {x = game.bonus.x - 0.5, y = game.bonus.y - 0.5},
                        color = {225,225,255,255},
                        time = globals.CurTime(),
                        amount = levelsettings[game.clvl].bonus
                    }
                    table.insert(scores, score)
                    game.bonus = nil
                end
            end

            if game.dots <= 0 then
                game.levelswitch = 1
                game.levelswitchtime = globals.CurTime()
            end
        
        elseif not player.dead then
            if globals.CurTime() - 2 >= game.starttime and game.started then
                game.ready = true
            end
            if input.IsButtonPressed(32) and not game.started then
                game.started = true
                game.starttime = globals.CurTime()
            end
        end

        if player.dead and not (player.deadtime + 2 > globals.CurTime()) then
            local add = player.deadtime + 2 - globals.CurTime()
            player.deathanim = math.abs(math.floor(add * 7.5))
            if player.deathanim > 15 then
                if game.lives <= 0 then
                    reset(true)
                    game.lives = 3
                    game.level = 1
                    game.score = 0
                    game.started = false
                else
                    reset(false)
                    for k, v in pairs(ghosts) do
                        ghosts[k].state = game.curstate
                    end
                end
            end
            if player.deathanim > 11 then player.deathanim = 11 end
        end

        if game.levelswitch ~= 0 then
            if globals.CurTime() - game.levelswitchtime >= 2 then
                game.level = game.level + 1
                reset(true)
            else
                local step = math.abs(math.floor(game.levelswitchtime - globals.CurTime() * 5))
                if step % 2 == 1 then
                    game.levelswitch = 1
                else
                    game.levelswitch = 2
                end
            end
        end
    else
        if input.IsButtonPressed(32) then
           game.paused = false
        end 
    end
end

