            if game.levelswitch == 0 then
                if ghosts.blinky.state == "fright" then
                    draw.SetTexture(sprites.ghosts.frightened[ghosts.blinky.animstate + ghosts.blinky.flash])
                elseif ghosts.blinky.state == "dead" then
                    draw.SetTexture(sprites.ghosts.dead[ghosts.blinky.direction])
                else
                    draw.SetTexture(sprites.ghosts.blinky[ghosts.blinky.direction][ghosts.blinky.animstate])
                end
                draw.FilledRect(boardpos.x + (ghosts.blinky.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (ghosts.blinky.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (ghosts.blinky.realpos.x - 0.5) * 20 + 6, boardpos.y + (ghosts.blinky.realpos.y - 0.5) * 20 + 6)

                if ghosts.pinky.state == "fright" then
                    draw.SetTexture(sprites.ghosts.frightened[ghosts.pinky.animstate + ghosts.pinky.flash])
                elseif ghosts.pinky.state == "dead" then
                    draw.SetTexture(sprites.ghosts.dead[ghosts.pinky.direction])
                else
                    draw.SetTexture(sprites.ghosts.pinky[ghosts.pinky.direction][ghosts.pinky.animstate])
                end
                draw.FilledRect(boardpos.x + (ghosts.pinky.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (ghosts.pinky.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (ghosts.pinky.realpos.x - 0.5) * 20 + 6, boardpos.y + (ghosts.pinky.realpos.y - 0.5) * 20 + 6)

                if ghosts.inky.state == "fright" then
                    draw.SetTexture(sprites.ghosts.frightened[ghosts.inky.animstate + ghosts.inky.flash])
                elseif ghosts.inky.state == "dead" then
                    draw.SetTexture(sprites.ghosts.dead[ghosts.inky.direction])
                else
                    draw.SetTexture(sprites.ghosts.inky[ghosts.inky.direction][ghosts.inky.animstate])
                end
                draw.FilledRect(boardpos.x + (ghosts.inky.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (ghosts.inky.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (ghosts.inky.realpos.x - 0.5) * 20 + 6, boardpos.y + (ghosts.inky.realpos.y - 0.5) * 20 + 6)

                if ghosts.clyde.state == "fright" then
                    draw.SetTexture(sprites.ghosts.frightened[ghosts.clyde.animstate + ghosts.clyde.flash])
                elseif ghosts.clyde.state == "dead" then
                    draw.SetTexture(sprites.ghosts.dead[ghosts.clyde.direction])
                else
                    draw.SetTexture(sprites.ghosts.clyde[ghosts.clyde.direction][ghosts.clyde.animstate])
                end    
                draw.FilledRect(boardpos.x + (ghosts.clyde.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (ghosts.clyde.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (ghosts.clyde.realpos.x - 0.5) * 20 + 6, boardpos.y + (ghosts.clyde.realpos.y - 0.5) * 20 + 6)
            end

            if player.powerup then
                draw.SetTexture(sprites.pacman.move[player.direction][player.animstate])
                draw.FilledRect(boardpos.x + (player.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (player.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (player.realpos.x - 0.5) * 20 + 6, boardpos.y + (player.realpos.y - 0.5) * 20 + 6)
            end
        else
            draw.SetTexture(sprites.pacman.death[player.deathanim])
            draw.FilledRect(boardpos.x + (player.realpos.x - 1 - 0.5) * 20 - 6, boardpos.y + (player.realpos.y - 1 - 0.5) * 20 - 6, boardpos.x + (player.realpos.x - 0.5) * 20 + 6, boardpos.y + (player.realpos.y - 0.5) * 20 + 6)
        end

        if debug and game.levelswitch == 0 and not player.dead then

            if ghosts.blinky.goal.x ~= nil and ghosts.blinky.state ~= "fright" then
                draw.Color(0,0,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.blinky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.blinky.goal.y + 0.5 - 1) * 20, 6)
                draw.Color(255,0,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.blinky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.blinky.goal.y + 0.5 - 1) * 20, 5)
            end

            if ghosts.pinky.goal.x ~= nil and ghosts.pinky.state ~= "fright" then
                draw.Color(0,0,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.pinky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.pinky.goal.y + 0.5 - 1) * 20, 6)
                draw.Color(255,75,255,255)
                draw.FilledCircle(boardpos.x + (ghosts.pinky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.pinky.goal.y + 0.5 - 1) * 20, 5)
            end

            if ghosts.inky.goal.x ~= nil and ghosts.inky.state ~= "fright" then
                draw.Color(0,0,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.inky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.goal.y + 0.5 - 1) * 20, 6)
                draw.Color(0,255,255,255)
                draw.FilledCircle(boardpos.x + (ghosts.inky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.goal.y + 0.5 - 1) * 20, 5)
            end

            if ghosts.clyde.goal.x ~= nil and ghosts.clyde.state ~= "fright" then
                draw.Color(0,0,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.clyde.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.clyde.goal.y + 0.5 - 1) * 20, 6)
                draw.Color(255,150,0,255)
                draw.FilledCircle(boardpos.x + (ghosts.clyde.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.clyde.goal.y + 0.5 - 1) * 20, 5)
            end

            if ghosts.clyde.state == "chase" then
                draw.Color(255,150,0,150)
                draw.OutlinedCircle(boardpos.x + (player.realpos.x + 0.5 - 1) * 20, boardpos.y + (player.realpos.y + 0.5 - 1) * 20, 8*20)
            end

            if ghosts.inky.state == "chase" then
                draw.Color(0,255,255,150)
                draw.OutlinedCircle(boardpos.x + (ghosts.inky.temppos.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.temppos.y + 0.5 - 1) * 20, 4)
                draw.Color(255,0,0,150)
                draw.Line(boardpos.x + (ghosts.inky.temppos.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.temppos.y + 0.5 - 1) * 20, boardpos.x + (ghosts.blinky.position.x + 0.5 - 1) * 20, boardpos.y + (ghosts.blinky.position.y + 0.5 - 1) * 20 )
                draw.Color(0,255,255,150)
                draw.Line(boardpos.x + (ghosts.inky.temppos.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.temppos.y + 0.5 - 1) * 20, boardpos.x + (ghosts.inky.goal.x + 0.5 - 1) * 20, boardpos.y + (ghosts.inky.goal.y + 0.5 - 1) * 20 )
            end

        end

        draw.SetFont(font)

        if #scores ~= 0 then
            for i, v in ipairs(scores) do
                local alpha = 255 - (globals.CurTime() - v.time) * 175
                if alpha <= 0 then 
                    alpha = 0
                    table.remove(scores, i)
                end
                local posx = boardpos.x + (v.pos.x - 0.5) * 20 - draw.GetTextSize(v.amount) / 2
                local posy = boardpos.y + (v.pos.y - 0.5) * 20
                posy = posy - (globals.CurTime() - v.time) * 50
                draw.Color(v.color[1], v.color[2], v.color[3], alpha)
                draw.Text(posx, posy, v.amount)
            end
        end

        draw.Color(255,255,0,255)
        if game.paused then
            draw.TextShadow(boardpos.x + #board * 20 / 2 - draw.GetTextSize("SPACE to resume") / 2 , boardpos.y + 403, "SPACE to resume")
        elseif not game.started then
            draw.TextShadow(boardpos.x + #board * 20 / 2 - draw.GetTextSize("SPACE to start") / 2 , boardpos.y + 403, "SPACE to start")
        elseif not game.ready then
            draw.TextShadow(boardpos.x + #board * 20 / 2 - draw.GetTextSize("READY!") / 2 , boardpos.y + 403, "READY!")
        end

        draw.Color(255,255,255,255)
        draw.TextShadow(boardpos.x + #board * 20 / 2 - draw.GetTextSize("HIGH SCORE") / 2, boardpos.y + 15, "HIGH SCORE")
        draw.TextShadow(boardpos.x + #board * 20 / 2 - draw.GetTextSize(game.highscore) / 2, boardpos.y + 40, game.highscore)
        draw.TextShadow(boardpos.x + 55 - draw.GetTextSize("SCORE") / 2, boardpos.y + 15, "SCORE")
        draw.TextShadow(boardpos.x + 55 - draw.GetTextSize(game.score) / 2, boardpos.y + 40, game.score)

        draw.SetTexture(sprites.pacman.move["left"][2])
        for i = 1, game.lives do
            draw.FilledRect(boardpos.x + 20 + (i - 1) * 40, boardpos.y + 36 * 20 - 40, boardpos.x + 20 + (i - 1) * 40 + 32, boardpos.y + 36 * 20 - 8)
        end

        local levelsort = 0
        for i = game.level - 7, game.level do
            local lvl = i
            if lvl > 21 then lvl = 21 end
            if lvl >= 1 then
                draw.SetTexture(sprites.icons[levelsettings[lvl].icon])
                draw.FilledRect(boardpos.x + #board * 20 - 20 - levelsort * 40, boardpos.y + 36 * 20 - 40, boardpos.x + #board * 20 - 20 - levelsort * 40 - 32, boardpos.y + 36 * 20 - 8)
                levelsort = levelsort + 1
            end
        end

        draw.SetFont(icons)

        if inRect(mx,my, boardpos.x + 27 * 20 - 10 + 2, boardpos.y + 5 + 3, boardpos.x + 27 * 20 - 10 + 20, boardpos.y + 5 + 20 + 1) then
            draw.Color(125,125,125,255)
            if input.IsButtonDown(1) then
                draw.Color(175,175,175,255)
            end
            if input.IsButtonReleased(1) then
                game.paused = true
                game.draw = false
                active:SetValue(false)
            end
        else
            draw.Color(75,75,75,255)
        end
        draw.Text(boardpos.x + 27 * 20 - 10, boardpos.y + 5, "r")
        if inRect(mx,my, boardpos.x + 26 * 20 - 10 + 2, boardpos.y + 5 + 3, boardpos.x + 26 * 20 - 10 + 20, boardpos.y + 5 + 20 + 1) then
                if debug then
                    draw.Color(85,85,85,255)
                else
                    draw.Color(75,75,75,255)
                end
            if input.IsButtonDown(1) then
                draw.Color(100,100,100,255)
            end
            if input.IsButtonReleased(1) then
                if debug then debug = false else debug = true end
            end
        else
            if debug then
                draw.Color(60,60,60,255)
            else
                draw.Color(40,40,40,255)
            end
        end
        draw.Text(boardpos.x + 26 * 20 - 10, boardpos.y + 5, "!")
    end
end

callbacks.Register("Draw", drawing)